import { createStore } from "redux"

function countReducer(state={count:0},action){
    switch(action.type){
        case "increment":return{
            count:state.count+1
        }
        case "decrement":return{
            count:state.count-1
        }
        return state
    }
}

const store=createStore(countReducer)
export default store
